import React, { useState } from 'react';

const UserManager = () => {
    const [inputValue, setInputValue] = useState('');
    const [users, setUsers] = useState([]); // Это будет загружаться из API в реальном проекте

    const handleAddUser = () => {
        const newUsers = inputValue.split('\n').map(user => user.trim()).filter(user => user);
        setUsers([...users, ...newUsers]);
        setInputValue('');
        // Логика добавления пользователей пачкой
    };

    const handleDeleteUser = (user) => {
        setUsers(users.filter(u => u !== user));
        // Логика удаления пользователя
    };

    const handleDeleteAllUsers = () => {
        if (window.confirm('Are you sure you want to delete all users?')) {
            setUsers([]);
            // Логика удаления всех пользователей
        }
    };

    return (
        <div>
            <h2>User Management</h2>
            <textarea
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                placeholder="Enter usernames or chat IDs, one per line"
            />
            <br />
            <button onClick={handleAddUser}>Add Users/Chats</button>
            <button onClick={handleDeleteAllUsers}>Delete All Users</button>
            <ul>
                {users.map(user => (
                    <li key={user}>
                        {user} <button onClick={() => handleDeleteUser(user)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default UserManager;
