import React, { useState } from 'react';

function MessageSender() {
    const [message, setMessage] = useState('');

    const handleSendMessage = () => {
        console.log('Sending message:', message);
        // Вставьте код для отправки сообщения через API
    };

    return (
        <div style={{ marginBottom: '20px' }}>
            <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                rows="4"
                cols="50"
                placeholder="Type your message here..."
                style={{ width: '100%', padding: '10px', fontSize: '16px', borderRadius: '5px' }}
            />
            <div style={{ marginTop: '10px' }}>
                <button
                    onClick={handleSendMessage}
                    style={{ padding: '10px 20px', fontSize: '16px', cursor: 'pointer', borderRadius: '5px', backgroundColor: '#61dafb', color: '#000' }}
                >
                    Send Message
                </button>
            </div>
        </div>
    );
}

export default MessageSender;
