import React, { useState } from 'react';
import '../styles/UserManager.css';

function UserManager() {
  const [userInput, setUserInput] = useState('');

  const handleAddUser = () => {
    // логика для добавления пользователя
    console.log('User added:', userInput);
    setUserInput('');
  };

  const handleDeleteAllUsers = () => {
    if (window.confirm('Are you sure you want to delete all users?')) {
      // логика для удаления всех пользователей
      console.log('All users deleted');
    }
  };

  return (
    <div className="user-manager">
      <h2>User Management</h2>
      <input
        type="text"
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
        placeholder="Enter username or chat ID"
      />
      <button className="button-64" onClick={handleAddUser}>
        <span className="text">Add User/Chat</span>
      </button>
      <button className="button-64 delete-button" onClick={handleDeleteAllUsers}>
        <span className="text">Delete All Users</span>
      </button>
    </div>
  );
}

export default UserManager;
