import React, { useState } from 'react';
import './MessageSender.css'; // Подключаем CSS

const MessageSender = () => {
    const [message, setMessage] = useState('');
    const [sending, setSending] = useState(false);

    const handleSendMessage = () => {
        setSending(true);
        // Логика отправки сообщения
    };

    const handleStopSending = () => {
        setSending(false);
        // Логика остановки отправки сообщения
    };

    return (
        <div className="message-sender">
            <h2>Message Sender</h2>
            <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Type your message here..."
            />
            <br />
            <div className="button-container">
                <button className="button-64" role="button" onClick={handleSendMessage} disabled={sending}>
                    <span className="text">Send Message</span>
                </button>
                <button className="button-64" role="button" onClick={handleStopSending} disabled={!sending}>
                    <span className="text">Stop Sending Message</span>
                </button>
            </div>
        </div>
    );
};

export default MessageSender;
