import React, { useState } from 'react';
import './UserManager.css'; // Подключаем CSS

const UserManager = () => {
    const [inputValue, setInputValue] = useState('');
    const [users, setUsers] = useState([]); // Это будет загружаться из API в реальном проекте

    const handleAddUser = () => {
        const newUsers = inputValue.split('\n').map(user => user.trim()).filter(user => user);
        setUsers([...users, ...newUsers]);
        setInputValue('');
        // Логика добавления пользователей пачкой
    };

    const handleDeleteUser = (user) => {
        setUsers(users.filter(u => u !== user));
        // Логика удаления пользователя
    };

    const handleDeleteAllUsers = () => {
        if (window.confirm('Are you sure you want to delete all users?')) {
            setUsers([]);
            // Логика удаления всех пользователей
        }
    };

    return (
        <div className="user-manager">
            <h2>User Management</h2>
            <textarea
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                placeholder="Enter usernames or chat IDs, one per line"
            />
            <br />
            <div className="button-container">
                <button className="button-64" role="button" onClick={handleAddUser}>
                    <span className="text">Add Users/Chats</span>
                </button>
                <button className="button-64" role="button" onClick={handleDeleteAllUsers}>
                    <span className="text">Delete All Users</span>
                </button>
            </div>
            <ul>
                {users.map(user => (
                    <li key={user}>
                        {user} <button className="button-64 small-button" role="button" onClick={() => handleDeleteUser(user)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default UserManager;
