import React, { useState } from 'react';
import '../styles/UserManager.css';
import '../styles/buttons.css';

function UserManager() {
    const [userInput, setUserInput] = useState('');
    const [users, setUsers] = useState([]);

    const handleAddUser = () => {
        if (userInput) {
            setUsers([...users, userInput]);
            setUserInput('');
        }
    };

    const handleDeleteUser = (userToDelete) => {
        setUsers(users.filter(user => user !== userToDelete));
    };

    const handleDeleteAllUsers = () => {
        if (window.confirm("Are you sure you want to delete all users?")) {
            setUsers([]);
        }
    };

    return (
        <div className="user-manager-container">
            <h2>User Management</h2>
            <div className="add-user-container">
                <input
                    className="user-input"
                    placeholder="Enter username or chat ID"
                    value={userInput}
                    onChange={(e) => setUserInput(e.target.value)}
                />
                <button className="button-64" onClick={handleAddUser}>
                    <span>Add User/Chat</span>
                </button>
            </div>
            <div className="users-list">
                {users.map((user, index) => (
                    <div key={index} className="user-item">
                        {user}
                        <button className="button-64" onClick={() => handleDeleteUser(user)}>
                            <span>Delete</span>
                        </button>
                    </div>
                ))}
            </div>
            <button className="button-64" onClick={handleDeleteAllUsers}>
                <span>Delete All Users</span>
            </button>
        </div>
    );
}

export default UserManager;
