import React, { useState } from 'react';
import MessageSender from './components/MessageSender';
import UserManager from './components/UserManager';
import './styles/App.css';

function App() {
    const [users, setUsers] = useState([]);

    const handleSendMessage = (message) => {
        console.log('Sending message:', message);
        // Implement the logic to send the message to all users/chats
    };

    const handleStopMessage = () => {
        console.log('Stopping message sending');
        // Implement the logic to stop message sending
    };

    const handleAddUser = (user) => {
        setUsers([...users, user]);
    };

    const handleDeleteUser = (userToDelete) => {
        setUsers(users.filter((user) => user !== userToDelete));
    };

    const handleDeleteAllUsers = () => {
        setUsers([]);
    };

    return (
        <div className="app">
            <MessageSender
                onSendMessage={handleSendMessage}
                onStopMessage={handleStopMessage}
            />
            <UserManager
                users={users}
                onAddUser={handleAddUser}
                onDeleteUser={handleDeleteUser}
                onDeleteAllUsers={handleDeleteAllUsers}
            />
        </div>
    );
}

export default App;
