import React, { useState } from 'react';

function UserManager() {
    const [users, setUsers] = useState([
        // Начальные пользователи для примера
        { id: 1, username: 'User1', firstname: 'John', type: 'User' },
        { id: 2, username: 'Chat1', firstname: null, type: 'Chat' }
    ]);
    const [newUser, setNewUser] = useState('');

    const handleAddUser = () => {
        if (newUser.trim()) {
            const newId = users.length + 1;
            const newEntry = { id: newId, username: newUser, firstname: null, type: 'User' };
            setUsers([...users, newEntry]);
            setNewUser('');
        }
    };

    const handleDeleteUser = (id) => {
        setUsers(users.filter(user => user.id !== id));
    };

    return (
        <div>
            <h3>Users/Chats List</h3>
            <ul>
                {users.map(user => (
                    <li key={user.id} style={{ marginBottom: '10px' }}>
                        {user.type}: {user.username || user.id} 
                        <button onClick={() => handleDeleteUser(user.id)} style={{ marginLeft: '10px', cursor: 'pointer' }}>
                            Delete
                        </button>
                    </li>
                ))}
            </ul>
            <div style={{ marginTop: '20px' }}>
                <input
                    type="text"
                    value={newUser}
                    onChange={(e) => setNewUser(e.target.value)}
                    placeholder="Enter username or chat ID"
                    style={{ padding: '10px', fontSize: '16px', width: '300px' }}
                />
                <button
                    onClick={handleAddUser}
                    style={{ marginLeft: '10px', padding: '10px 20px', fontSize: '16px', cursor: 'pointer', borderRadius: '5px', backgroundColor: '#61dafb', color: '#000' }}
                >
                    Add User/Chat
                </button>
            </div>
        </div>
    );
}

export default UserManager;
