import React, { useState } from 'react';
import MessageSender from './components/MessageSender';
import UserManager from './components/UserManager';

const App = () => {
    const [view, setView] = useState('messageSender');

    const switchToUserManager = () => setView('userManager');
    const switchToMessageSender = () => setView('messageSender');

    return (
        <div>
            {view === 'messageSender' && (
                <>
                    <MessageSender />
                    <button onClick={switchToUserManager}>Edit Users/Chats</button>
                </>
            )}
            {view === 'userManager' && (
                <>
                    <UserManager />
                    <button onClick={switchToMessageSender}>Back to Message Sender</button>
                </>
            )}
        </div>
    );
};

export default App;
