import React, { useState } from 'react';
import '../styles/UserManager.css';

const UserManager = () => {
  const [users, setUsers] = useState([]); // Состояние для хранения списка пользователей
  const [inputValue, setInputValue] = useState('');

  const handleAddUser = () => {
    if (inputValue.trim()) {
      setUsers([...users, { id: users.length + 1, name: inputValue }]);
      setInputValue('');
    }
  };

  const handleDeleteUser = (id) => {
    setUsers(users.filter(user => user.id !== id));
  };

  const handleDeleteAllUsers = () => {
    const confirmDelete = window.confirm('Are you sure you want to delete all users?');
    if (confirmDelete) {
      setUsers([]);
    }
  };

  return (
    <div className="user-manager">
      <h2>User Management</h2>
      <input
        type="text"
        placeholder="Enter username or chat ID"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
      />
      <button className="add-user-btn" onClick={handleAddUser}>
        Add User/Chat
      </button>
      <button className="delete-all-btn" onClick={handleDeleteAllUsers}>
        Delete All Users
      </button>

      <ul className="user-list">
        {users.map((user) => (
          <li key={user.id}>
            {user.name}
            <button className="delete-btn" onClick={() => handleDeleteUser(user.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserManager;
